package ru.mycompany.examples;

import java.util.List;

public class TestSQLite {
  public static void main(String[] args) {
    SQLiteProvider provider = SQLiteProvider.getInstance();
    List<User> accountsList = provider.getAccountsList("profitcentr");
    if (accountsList == null){
      System.out.println("Ошибка от БД");
      return;
    }
    for (User user : accountsList){
      System.out.println("Login: " + user.getLogin() + "   password: " + user.getPassword());
    }
    System.out.println(provider.isExistImageInProfitCentrTable("background-image:url(data:image/jpg;base64,);"));
  }
}
